import React, {Fragment} from 'react'
import { useForm } from 'react-hook-form';

const EjemploUno = () => {
  const { register, formState: { errors } , handleSubmit } = useForm();

  const enviarForm = (data) => {
    console.log("data: ", data);
  }

  return ( 
    <Fragment>
      <h1>Ejemplo 1</h1>
      <form onSubmit={handleSubmit(enviarForm)}>
        <input 
          name="Título" placeholder='Ingresar título'
          className='form-control my-2'
          {...register("Título", { required: { value: true, message: 'Título obligatorio' } })}
        />
        {errors.titulo && <span className='text-danger text-small d-block mb-2'>{errors.titulo.message}</span>}
        <input 
          name="Descripción" placeholder='Ingresar descripción'
          className='form-control my-2'
          {...register("Descripción", { required: { value: true, message: 'Descripción obligatoria' }, 
                                        maxLength: {value: 100, message: 'Cantidad máxima 100 caracteres.'}
                                      })}
        />
        {errors.titulo && <span className='text-danger text-small d-block mb-2'>{errors.titulo.message}</span>}
        <button className='btn btn-primary'>Enviar</button>
      </form>
    </Fragment>
  );
}
 
export default EjemploUno;
